import './App.css';
import React from 'react';
import Table from './components/Table';
import Form from './components/Form';
import { getMethod, postMethod } from './services/api.js';
import Axios from 'axios';

class App extends React.Component {
  constructor() {
    super ();

    this.state = {
      fullName: '',
      cpf: '',
      plate: '',
      telephone: '',
      email: '',
      accident: '',
      date: '',
      value: '',
      request: '',
      sinistros: [],
    };
  }

  componentDidMount() {
    this.fetchSinistros();
  }

  fetchSinistros = async () => {
    Axios.get("http://localhost:3001/getMethod")
      .then((response) => {
        console.log(response);
      })
    // const arraySinistros = await getMethod();
    // this.setState({ sinistros: [ ...arraySinistros ] });
  }

  handleInputChange = ({ target }) => {
    this.setState({
      [target.name]: target.type === 'checked' ? target.checked : target.value,
    }, this.handleSaveButtonDisable);
  }

  handleSaveButtonClick = ( event ) => {
    event.preventDefault();
    console.log('Entrou!')
    const {
      fullName,
      cpf,
      plate,
      telephone,
      email,
      accident,
      date,
      value,
      request,
      sinistros,
    } = this.state;

    console.log(this.state);

    const objectSinistro = {
      fullName,
      cpf,
      plate,
      telephone,
      email,
      accident,
      date,
      value,
      request,
    };

    Axios.post("http://localhost:3001/register", objectSinistro)
      .then((response) => console.log(response));

    this.setState({
      sinistros: [...sinistros, objectSinistro],
      fullName: '',
      cpf: '',
      plate: '',
      telephone: '',
      email: '',
      accident: '',
      date: '',
      value: '',
      request: '',
    });
  }

  render() {
    // console.log(...this.state.sinistros);
    return (
      <div className="App">
        <div className="left">
          <h1>MAPFRE</h1>
          <Form 
            { ...this.state }
            onInputChange={ this.handleInputChange }
            onSaveButtonClick={ this.handleSaveButtonClick }
          />
        </div>
        <div className="right">
          <h5>CONSULTA DE SINISTROS</h5>
            <Table 
              { ...this.state }
            />
        </div>
      </div>
    );
  }
}

export default App;
