import './App.css';
import React from 'react';
import Table from './components/Table'
import Form from './components/Form'
import loadSinistros from './services/api.js'

class App extends React.Component {
  constructor() {
    super ();

    this.state = {
      fullName: '',
      cpf: '',
      plate: '',
      telephone: '',
      email: '',
      accident: '',
      date: '',
      value: '',
      request: '',
      sinistros: [],
    };
  }

  componentDidMount() {
    this.fetchSinistros();
  }

  fetchSinistros = async () => {
    const arraySinistros = await loadSinistros();
    this.setState({ sinistros: [ ...arraySinistros ] });
  }

  handleInputChange = ({ target }) => {
    this.setState({
      [target.name]: target.type === 'checked' ? target.checked : target.value,
    }, this.handleSaveButtonDisable);
  }

  render() {
    console.log(this.state.sinistros);
    return (
      <div className="App">
        <div className="left">
          <h1>MAPFRE</h1>
          <Form 
            { ...this.state }
            onInputChange={ this.handleInputChange }
            onSaveButtonClick={ this.handleSaveButtonClick }
          />
        </div>
        <div className="right">
          <p>Consulta de Sinistros</p>
            <Table 
              { ...this.state.sinistros }
            />
        </div>
      </div>
    );
  }
}

export default App;
